package n1087;

import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int n=sc.nextInt();
		int i=0;
		int s=0;
		while(s<n) {
			i=i+1;
			s=s+i;
		}
		System.out.println(s);
			
		
		
	}

}
